CREATE TABLE IF NOT EXISTS `ports_cbqos` (  `cbqos_id` int(11) NOT NULL AUTO_INCREMENT,  `device_id` int(11) NOT NULL,  `port_id` int(11) NOT NULL,  `direction` varchar(16) COLLATE utf8_unicode_ci NOT NULL,  `PrePolicyPkt` int(11) NOT NULL,  `PrePolicyPkt_rate` int(11) NOT NULL,  `PrePolicyByte` int(11) NOT NULL,  `PrePolicyByte_rate` int(11) NOT NULL,  `PostPolicyByte` int(11) NOT NULL,  `PostPolicyByte_rate` int(11) NOT NULL,  `DropPkt` int(11) NOT NULL,  `DropPkt_rate` int(11) NOT NULL,  `DropByte` int(11) NOT NULL,  `DropByte_rate` int(11) NOT NULL,  `NoBufDropPkt` int(11) NOT NULL,  `NoBufDropPkt_rate` int(11) NOT NULL, `cbqos_lastpolled` int(11) NOT NULL,  `policy_index` int(11) NOT NULL,  `object_index` int(11) NOT NULL,  PRIMARY KEY (`cbqos_id`),  UNIQUE KEY `device_id` (`device_id`,`port_id`,`policy_index`,`object_index`)) ENGINE=MEMORY  DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci ;
