CREATE TABLE IF NOT EXISTS `devices_mibs` (`mib_id` int(11) NOT NULL AUTO_INCREMENT,  `device_id` int(11) NOT NULL,  `mib` varchar(128) COLLATE utf8_unicode_ci NULL,  `table_name` varchar(128) COLLATE utf8_unicode_ci NULL,  `oid` varchar(128) COLLATE utf8_unicode_ci NULL,  `disabled` tinyint(1) NOT NULL DEFAULT '0',  `updated` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,  PRIMARY KEY (`mib_id`),  KEY `mib` (`mib`,`table_name`,`oid`)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='Stores disabled MIBs or combination MIB with tables/oids';