DROP TABLE IF EXISTS `wifi_radios`;
CREATE TABLE `wifi_radios` (  `wifi_radio_id` int(11) NOT NULL AUTO_INCREMENT,  `device_id` int(11) NOT NULL,  `radio_mib` varchar(32) COLLATE utf8_unicode_ci NOT NULL,  `radio_number` int(11) NOT NULL,  `radio_type` varchar(45) COLLATE utf8_unicode_ci DEFAULT NULL,  `radio_protection` varchar(8) COLLATE utf8_unicode_ci NOT NULL DEFAULT 'none',  `radio_bsstype` varchar(16) COLLATE utf8_unicode_ci DEFAULT NULL,  `radio_status` varchar(45) COLLATE utf8_unicode_ci DEFAULT NULL,  `radio_ap` int(11) NOT NULL,  `radio_clients` int(11) DEFAULT NULL,  `radio_txpower` varchar(8) COLLATE utf8_unicode_ci DEFAULT NULL,  `radio_channel` int(11) DEFAULT NULL,  `radio_mac` varchar(45) COLLATE utf8_unicode_ci DEFAULT NULL,  PRIMARY KEY (`wifi_radio_id`),  UNIQUE KEY `unique_dev_ap_number` (`device_id`,`radio_ap`,`radio_number`)) ENGINE=InnoDB  DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;
