CREATE TABLE IF NOT EXISTS `wifi_wlans` ( `wlan_id` int(11) NOT NULL AUTO_INCREMENT, `device_id` int(11) NOT NULL, `wlan_index` int(11) NOT NULL, `wlan_name` varchar(128) DEFAULT NULL, `wlan_admin_status` tinyint(1) NOT NULL DEFAULT '1', `wlan_ssid` varchar(64) NOT NULL, `wlan_ssid_bcast` tinyint(1) NOT NULL, `wlan_bssid` varchar(64) NOT NULL, `wlan_bss_type` varchar(32) NOT NULL, `wlan_channel` int(11) NOT NULL, `wlan_dtim_period` int(11) NOT NULL, `wlan_beacon_period` int(11) NOT NULL, `wlan_frag_thresh` int(11) NOT NULL, `wlan_igmp_snoop` tinyint(1) NOT NULL, `wlan_prot_mode` varchar(32) NOT NULL, `wlan_radio_mode` varchar(32) NOT NULL, `wlan_rts_thresh` int(11) NOT NULL, `wlan_vlan_id` int(11) NOT NULL, PRIMARY KEY (`wlan_id`), KEY `device_id` (`device_id`)) ENGINE=InnoDB DEFAULT CHARSET=utf8;
ALTER TABLE  `ports`   ADD  `port_label_short` VARCHAR( 16 ) NOT NULL AFTER  `port_label` ;
ALTER TABLE  `devices` ADD  `force_discovery` TINYINT( 1 ) NOT NULL DEFAULT '0' AFTER  `uptime` ;
