CREATE TABLE IF NOT EXISTS `p2p_radios` (  `radio_id` int(11) NOT NULL AUTO_INCREMENT,  `device_id` int(11) NOT NULL,  `radio_mib` varchar(32) COLLATE utf8_unicode_ci NOT NULL,  `radio_index` varchar(32) COLLATE utf8_unicode_ci NOT NULL,  `radio_name` varchar(32) COLLATE utf8_unicode_ci NOT NULL,  `radio_standard` varchar(4) COLLATE utf8_unicode_ci DEFAULT NULL,  `radio_modulation` varchar(8) COLLATE utf8_unicode_ci DEFAULT NULL,  `radio_rx_level` float DEFAULT NULL,  `radio_tx_power` float DEFAULT NULL,  `radio_rx_freq` int(11) DEFAULT NULL,  `radio_tx_freq` int(11) DEFAULT NULL,  `radio_bandwidth` int(11) DEFAULT NULL,  `radio_e1t1_channels` int(11) DEFAULT NULL,  `radio_total_capacity` int(11) DEFAULT NULL,  `radio_cur_capacity` int(11) DEFAULT NULL,  `radio_eth_capacity` int(11) DEFAULT NULL,  `radio_loopback` tinyint(1) DEFAULT NULL,  `radio_tx_mute` tinyint(1) DEFAULT NULL,  `deleted` tinyint(1) NOT NULL DEFAULT '0',  PRIMARY KEY (`radio_id`),  UNIQUE KEY `unique_index` (`device_id`,`radio_mib`,`radio_index`)) ENGINE=InnoDB  DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci ;
