CREATE TABLE IF NOT EXISTS `oids` (  `oid_id` int(11) NOT NULL AUTO_INCREMENT,  `oid` varchar(128) COLLATE utf8_unicode_ci NOT NULL,  `oid_type` varchar(8) COLLATE utf8_unicode_ci NOT NULL,  `oid_descr` varchar(128) COLLATE utf8_unicode_ci NOT NULL,  `oid_name` varchar(64) COLLATE utf8_unicode_ci NOT NULL,  `oid_unit` varchar(16) COLLATE utf8_unicode_ci DEFAULT NULL,  PRIMARY KEY (`oid_id`)) ENGINE=InnoDB  DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;
CREATE TABLE IF NOT EXISTS `oids_assoc` (  `oid_assoc_id` int(11) NOT NULL AUTO_INCREMENT,  `oid_id` int(11) NOT NULL,  `device_id` int(11) NOT NULL,  PRIMARY KEY (`oid_assoc_id`)) ENGINE=InnoDB  DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;
