CREATE TABLE `netscaler_servicegroupmembers` (  `svc_id` int(11) NOT NULL AUTO_INCREMENT,  `device_id` int(11) NOT NULL,  `svc_name` varchar(128) COLLATE utf8_unicode_ci NOT NULL,  `svc_groupname` varchar(128) COLLATE utf8_unicode_ci NOT NULL,  `svc_fullname` varchar(128) COLLATE utf8_unicode_ci DEFAULT NULL,  `svc_label` varchar(128) COLLATE utf8_unicode_ci DEFAULT NULL,  `svc_ip` varchar(128) COLLATE utf8_unicode_ci NOT NULL,  `svc_port` int(8) NOT NULL,  `svc_type` varchar(64) COLLATE utf8_unicode_ci NOT NULL,  `svc_state` varchar(32) COLLATE utf8_unicode_ci NOT NULL,  `svc_clients` int(11) NOT NULL,  `svc_server` int(11) NOT NULL,  `svc_req_rate` int(11) NOT NULL,  `svc_bps_in` int(11) NOT NULL,  `svc_bps_out` int(11) NOT NULL,  `svc_ignore` tinyint(1) NOT NULL DEFAULT '0',  `svc_ignore_until` int(11) NOT NULL DEFAULT '0',  PRIMARY KEY (`svc_id`),  KEY `device_id` (`device_id`,`svc_name`)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;
